import { fetchLinkList, deleteLink, addLink, updateLink } from '@/api/link'

export default {
  data() {
    return {
      data: {},
      list: [
        {
          id: null,
          linkName: '',
          linkUrl: '',
          linkOrder: ''
        }
      ],
      form: {
        id: null,
        linkName: '',
        linkUrl: '',
        linkOrder: ''
      },
      page: 1,
      size: 10,
      currRow: null,
      addVisible: false,
      editVisible: false
    }
  },
  created() {
    this.fetchData()
  },
  methods: {
    fetchData() {
      fetchLinkList(this.page, this.size).then(response => {
        this.list = response.data.list
        this.data = response.data
      })
    },
    calcIndex(index) {
      return this.size * (this.page - 1) + index + 1
    },
    handleAdd() {
      // reset
      this.form = {
        id: null,
        linkName: '',
        linkUrl: '',
        linkOrder: ''
      }
      this.addVisible = true
    },
    handleEdit() {
      if (this.currRow === null) {
        this.$message({
          type: 'warning',
          message: '请选择要修改的行'
        })
        return
      }
      this.form = this.currRow
      this.addVisible = true
    },
    handleDelete() {
      if (this.currRow === null) {
        this.$message({
          type: 'warning',
          message: '请选择要删除的行'
        })
        return
      }
      this.$confirm(`此操作将永久删除外链“ ${this.currRow.linkName} ”，是否继续?`, '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        deleteLink(this.currRow.id).then(response => {
          // 更新数据
          this.fetchData()
          this.$message({
            type: 'success',
            message: '删除成功!'
          })
          this.currRow = null
        })
      })
    },
    handleCurrentPageChange(currPage) {
      this.page = currPage
      this.fetchData()
    },
    handleCurrentRowChange(currRow) {
      this.currRow = currRow
    },
    onSubmit() {
      if (this.form.id) {
        updateLink(this.form).then(response => {
          this.addVisible = false
          // 更新数据
          this.fetchData()
          this.$message({
            type: 'success',
            message: '更新成功!'
          })
        })
      } else {
        addLink(this.form).then(response => {
          this.addVisible = false
          // 更新数据
          this.fetchData()
          this.$message({
            type: 'success',
            message: '添加成功!'
          })
        })
      }
    }
  }
}
