export default {
  model: {
    prop: 'image',
    event: 'change'
  },
  props: {
    image: String,
    height: String
  },
  data() {
    return {
      mHeight: '180px',
      imageSize: '',
      iconSize: '',
      action: process.env.VUE_APP_UPLOAD_URL
    }
  },
  created() {
    this.mHeight = this.height
    this.imageSize = 'height: ' + this.mHeight + ';'
    this.iconSize = 'height: ' + this.mHeight + ';width: ' + this.mHeight + ';line-height:' + this.mHeight
  },
  methods: {
    handleAvatarSuccess(response) {
      this.$emit('change', response.data.url)
    },
    beforeAvatarUpload(file) {
      const isLt4M = file.size / 1024 / 1024 < 4

      if (!isLt4M) {
        this.$message.error('上传头像图片大小不能超过 4MB!')
      }
      return isLt4M
    }
  }
}
